import "./App.css";
import { useEffect } from "react";
import { useForm } from "react-hook-form";
import TextField from "@mui/material/TextField";
import FormControl from "@mui/material/FormControl";

function App() {
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm();
  const onSubmit = (data) => console.log(data);

  useEffect(() => {
    console.log(errors);
  }, [errors]);

  return (
    <div className="body">
      <div className="formWrapper">
        <h1 className="title">React forms hook</h1>
        <form onSubmit={handleSubmit(onSubmit)}>
          <TextField
            fullWidth
            error={errors.name}
            id="name"
            label="Name"
            {...register("name", { required: true })}
            helperText={errors.name && "Field required"}
          />
          <TextField
            fullWidth
            error={errors.email}
            id="email"
            label="Email"
            {...register("email", {
              required: true,
              pattern:
                /^[a-zA-Z0-9.!#$%&'*+\/=?^_`{|}~-]+@[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?(?:\.[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?)*$/,
            })}
            helperText={errors.email && "Type a valid email"}
          />
          <TextField
            fullWidth
            error={errors.name}
            id="name"
            label="Name"
            {...register("name", { required: true })}
            helperText={errors.name && "Field required"}
          />

          <input type="submit" />
        </form>
      </div>
    </div>
  );
}

export default App;
