import { useEffect } from "react";
import { useForm } from "react-hook-form";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import { MuiThemeProvider, createMuiTheme } from "material-ui/styles";

import "./App.css";

const theme = createMuiTheme({
  palette: {
    secondary: "#11131b",
  },
});

function App() {
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm();
  const onSubmit = (data) => console.log(data);

  useEffect(() => {
    console.log(errors);
  }, [errors]);

  return (
    <div className="body">
      <div className="formWrapper">
        <h1 className="title">Form Validation</h1>
        <form onSubmit={handleSubmit(onSubmit)}>
          <TextField
            fullWidth
            error={errors.name}
            id="name"
            label="Name"
            {...register("name", { required: true })}
            helperText={errors.name && "Field required"}
            style={{ margin: "10px 0" }}
          />
          <TextField
            fullWidth
            error={errors.email}
            id="email"
            label="Email"
            {...register("email", {
              required: true,
              pattern:
                /^[a-zA-Z0-9.!#$%&'*+\/=?^_`{|}~-]+@[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?(?:\.[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?)*$/,
            })}
            helperText={errors.email && "Type a valid email"}
            style={{ margin: "10px 0" }}
          />
          <TextField
            fullWidth
            error={errors.name}
            id="password"
            label="Password"
            type="password"
            {...register("name", { required: true, minLength: 8 })}
            helperText={errors.name && "Field required minimum of 8 characters"}
            style={{ margin: "10px 0" }}
          />

          <MuiThemeProvider theme={theme}>
            <Button variant="contained" type="submit" color="secondary">
              Contained
            </Button>
          </MuiThemeProvider>
        </form>
      </div>
    </div>
  );
}

export default App;
